    company: Company;
    companyRetrieved = false;
    formsRetrieved = false;
    forms: Form[];
    cardHoverId = null;

    queryParams: Params = Object.assign({}, this.route.snapshot.queryParams);

    constructor(
        private companyService: CompanyService,
        private formMethodService: FormMethodService,
        private logService: LogService,
        private route: ActivatedRoute,
        private formService: FormService,
        private router: Router,
    ) {}

    ngOnInit() {
        this.getForms();
        this.getCompanyById();
    }

    async getCompanyById() {
        this.queryParams = await this.formMethodService.checkCompanyId(this.company, this.queryParams);
        this.companyService.getByCompanyId(this.queryParams['companyId'])
            .subscribe(company => {
                this.company = company['obj'];
                this.companyRetrieved = true;
            }, error => {
                this.logService.console(error, false);
            });
    }

    async getForms() {
        this.forms = await this.formService.getByCompanyAsync(this.queryParams.companyId);
        this.formsRetrieved = true;
    }

    routeTo(url: string) {
        this.router.navigate([url]);
    }

    returnTitle(title: string) {
        let newTitle = this.replaceAll(title, '-', ' ');

        return newTitle;
    }

    replaceAll(str, find, replace) {
        return str.replace(new RegExp(escapeRegExp(find), 'g'), replace);
    }

    returnUrl(form: Form) {
        try {
            let url = 'client-app/';
            if (form.isSimpleForm) {
              url += `simple/form`;
            } else {
                url += 'form/page/start';
            }
            this.queryParams['companyId'] = this.company.companyId;
            this.queryParams['formId'] = form.id;
            this.router.navigate([url], {queryParams: this.queryParams});
        } catch (error) {
            console.log(error);
        }
    }

    routeToLink(link: string) {
        window.open(link);
    }

    returnIconCheck(form: Form) {
        if (form && form.icon) {
            return form.icon;
        } else {
            return this.returnIcon(this.returnTitle(form.title))
        }
    }

